mapper.delete:

DELETE FROM r_product
WHERE product_option_pro_opt_id = #{pro_opt_id}
AND member_mem_id = #{mem_id}

mapper.select:
SELECT r.product_option_pro_opt_id, r.re_price, r.re_selldate, po.pro_opt_size, p.pro_name, pi.pro_loc FROM r_product r INNER JOIN product_option po ON r.product_option_pro_opt_id = po.pro_opt_id INNER JOIN product p ON p.pro_id = po.product_pro_id INNER JOIN pro_image pi on pi.product_pro_id = p.pro_id AND pi.pro_loc like('%medium%') INNER JOIN member m ON m.mem_id = r.member_mem_id WHERE m.mem_id = #{mem_id}
AND re_selldate BETWEEN TO_DATE(#{start_date}, 'YYYY-MM-DD') AND TO_DATE(#{end_date}, 'YYYY-MM-DD')+1
AND re_available = '1'

mapper.update:

	UPDATE order_detail SET order_detail_check = '0'
    WHERE order_total_order_no = #{order_no}
    AND product_option_pro_opt_id = #{pro_opt_id}

mapper.select:
SELECT COUNT(order_no)
FROM order_total
WHERE member_mem_id = #{mem_id}
